import './CardUser.scss';
import {useEffect, useState} from 'react'

export function CardUser(){
    const [users, setUsers] = useState(null);
    const [reloadUsers , setReloadUsers] = useState(false);

    useEffect(() =>{
        fetch('http://localhost:8000/api/users')
        .then(res => res.json())
        .then(
            (result) =>{
            setUsers(result)
            }
        )
    }, [reloadUsers]);
    console.log(users)
    return(
        <div className='position-relative-for-other-element'>
            <h1 className='title-player text-align-center'>siuvi de tous les joueurs</h1>
            <div className='flex wrap '>
                {users.map((user)=>{
                    <div className='flex card-user space-arround align-items-center'>
                    <div>
                        <p>prenom:{}</p>
                        <p>nom:</p>
                        <p>pseudo:</p>
                        <p>jeu:</p>
                    </div>
                    <div className='flex space-arround '>
                        <p>modifier</p>
                        <p>supprimer</p>
                    </div>
                </div>
                })}
                
            </div>
        </div>
    )
}
